import React, { Component } from 'react';
import '../css/App.css';

class TransitionComp extends Component{

    state = {
        show: true,
    }

    render(){
        return(
            <>
                {
                    this.state.show ?
                    <div style={{
                        background: 'red',
                        height: '100px',
                    }}></div>
                    :null
                }
                <div 
                    className="showDiv"
                    onClick={this.showDiv}
                >
                    Show/Hide
                </div>
            </>

        )
    }
}


export default TransitionComp;
